graphql.query:
mutation DeleteRecords($input: DeleteIDs!) {
  deleteResult(input: $input) {
 	status
    message
  }
}

mutation updateStatus($input: UpdateIDs!) {
  updateResult(input: $input) {
 	status
    message
  }
}

mutation modifyRecipe($input: inputFields!) {
  modifyResult(input: $input) {
  id
  category{
    id
    slug
    title
    description
  }
 slug
 title
  description
  remark
    ingredients{
      title
      amount
    }
    howto{
      orderStep
      title
      description
      remark
    } 
    responseStatus{
      status
      message
    }
  }
}

