graphql.query:
query ($key: searchCategories!) {
  viewCatgories(key:$key){
    rowCount 
    searchKey{
        type
        key
    }
    rows{ 
    	id 
    	slug
    	title
    	description
    	remark
    	createdBy{ 
            username
            fullName
            image
            website
            instagram
            facebook
        }
    }
  }
}

